import React, { Component } from 'react';
import Input from './input'
import Joi from "joi"
class LoginForm extends Component {
    state = { 
        account :{
            username: "",
            password: ""
        },
        errors:{}
        
     }
     schema = {
         username: Joi.string().required().label('Username'),
         password: Joi.string().required().label('Password')
     }
    handleSubmit = e=>{
        e.preventDefault();

        const errors = this.validate();
        this.setState({errors: errors || {}})
        if(errors) return;
    }
    handleChange = ({currentTarget: input})=>{
        const errors = {...this.state.errors};
        const errorMessage = this.validateProperty(input)
        if(errorMessage) errors[input.name] = errorMessage;
        else delete errors[input.name]
        const newAccount = {...this.state.account}
        newAccount[input.name] = input.value
        this.setState({account: newAccount, errors:errors})
    }
    validate = () => {
        const result = Joi.validate(this.state.account,this.schema, {abortEarly: false})
        if(!result.error) return null

        const errors = {}
        for(let item of result.error.details){
            errors[item.path[0]] = item.message
        }
        return errors;
    }
    validateProperty = ({name, value})=>{
        const propObj = {[name]:value}
        const schemaProperty = {[name]:this.schema[name]}
        const error = Joi.validate(propObj,schemaProperty)
        if(error) return null;
        return error.details[0].message;
    }

    render() { 
        return ( 
            <div class="offset-md-2 col-6">
                <form onSubmit={this.handleSubmit}>
                    <Input 
                    name="username" 
                    value={this.state.account.username}
                    label="username"
                    onChange={this.handleChange}
                    error = {this.state.errors.username}
                    />
                    <Input 
                    name="password" 
                    value={this.state.account.password}
                    label="password"
                    onChange={this.handleChange}
                    error = {this.state.errors.password}
                    />
                <button type="submit" class="btn btn-primary">Login</button>
                </form>
            </div>

         );
    }
}
 
export default LoginForm;
