import React, { Component } from 'react';

class Like extends Component {

    render() { 
        return (
            <i onClick={this.props.ClickHandler} className={SelectClass()}></i>
        );
    }
    
    SelectClass(){
        return `${this.props.IsLiked? "fas":"far"} fa-heart`
    }
    ClickHandler(){-
        this.setState({IsLiked: !this.state.IsLiked})
    }
}
 
export default Like;
