import React, { Component } from 'react';

class Like extends Component {
    state = { IsLiked : false }
    render() { 
        return (this.SelectView());
    }
    
    SelectView(){
        return this.state.IsLiked? <i onClick={this.ClickHandler} class="fas fa-heart"></i> : <i class="far fa-heart"></i>;
    }
    ClickHandler(){
        this.setState({IsLiked: !this.state.IsLiked})
    }
}
 
export default Like;
