import React, { Component } from 'react';

class Pagination extends Component {
    state = { 
        items : this.props.items,
        pageSize: 5,
        pageCount : Math.ceil(this.props.items.length/this.props.pageSize),
        pages: this.createNumArray()
     }
    render() { 
        if(this.pageCount == 1){
            return null
        }
        return ( 
            <nav aria-label="Page navigation example">
                <ul className="pagination">
                    {this.state.pages.map(item =>
                    <li className="page-item"><a className="page-link" href="#">{item}</a></li>
                    )}
                </ul>
            </nav>
         );
    }
    createNumArray(){
        let result = [];
        for(let i = 1; i<Math.ceil(this.props.items.length/this.props.pageSize+1);i++){
            result.push(i)
        }
        return result
    }
}
 
export default Pagination;
